import React, { useEffect, useState } from "react";
import Statistics from "../Statistics/Statistics";
import cn from 'classnames'

import s from "./Repressions.module.css";

type Data = {
  universities: any;
};

const Repressions: React.FC<Data> = ({ universities }) => {
  const [data, setData] = useState([]);
  const [active, setActive] = useState("all");
  useEffect(() => {
    if (universities) {
      setData(Object.values(universities));
    }
  }, [universities]);
  console.warn(data);

  const allPoliticalPrisoners = data.map((item: any) => item.political_prisoners_num).reduce((prev, next) => prev + next);

  return (
    <>
      <div className={s.container}>
        <div className={s.inner}>
          <div className={s.top}>
            <div className={s.line} />
            <p className={s.petition}>Репрессии</p>
          </div>
          <p className={s.title}>ЧТО ЖДЁТ ВАС НА ПРОГРАММЕ?</p>
          <p className={s.text}>
            В Беларуси у университетов нет автономии. Администрация не защищает{" "}
            <br />
            студентов, а выполняет приказы диктатора А. Лукашенко: ректоры и
            деканы <br /> отчисляют студентов, увольняют преподавателей,
            вызывают на протестующих <br /> ОМОН, пишут заявления в милицию.
          </p>
          <div className={s.bottom}>
            <p className={s.data}> Данные о репрессиях по всей Беларуси</p>
            <div className={s.buttons}>
              <button
                className={cn(active === "all" && s.buttonActive, s.button)}
              >
                ВСЕ
              </button>
              {data.map((item: any) => {
                return (
                  <button className={s.button} key={item?.id}>
                    {item.name_abr_ru}
                  </button>
                );
              })}
            </div>
          </div>
        </div>
      </div>
      <Statistics political={allPoliticalPrisoners}/>
    </>
  );
};

export default Repressions;
