import React, { useState, useEffect } from "react";
import cn from "classnames";

import vyasna from "../../assets/vyasna.png";
import Prisoner from "../../components/Prisoner/Prisoner";
import photo from "../../assets/photo.png";

import s from "./History.module.css";

type Props = {
  stories: any;
};

const History: React.FC<Props> = ({ stories }) => {
  const [data, setData] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState<string>();
  useEffect(() => {
    if (stories) {
      setData(Object.values(stories));
    }
  }, [stories]);

  const selectedStudentData: any = data?.filter((item: any) => {
    return item.id === selectedStudent;
  });

  console.warn(selectedStudentData);

  return (
    <div className={s.container}>
      <div className={s.inner}>
        <div className={s.top}>
          <p className={s.title}>Политические истории</p>
          <img src={vyasna} alt="vyasna" />
        </div>
        <p className={s.text}>
          Знакомьтесь с беларускими студентами.{" "}
          <strong>25 из них находятся в тюрьме </strong> <br />
          из-за своих политических взглядов.
        </p>
        <div className={s.info}>
          <Prisoner
            img={
              selectedStudentData[0] ? selectedStudentData[0].photo_url : photo
            }
            time={
              selectedStudentData[0] ? selectedStudentData[0].sanction_ru : ""
            }
            name={selectedStudentData[0] ? selectedStudentData[0].name_ru : ""}
            university={
              selectedStudentData[0] ? selectedStudentData[0].university_ru : ""
            }
            text={selectedStudentData[0] ? selectedStudentData[0].story_ru : ""}
          />
          <div className={s.list}>
            {data?.map((item: any) => {
              return (
                <div
                  className={cn(
                    item.id === selectedStudent && s.itemActive,
                    s.item
                  )}
                  style={{ backgroundImage: `url(${item.photo_url})` }}
                  onClick={() => setSelectedStudent(item.id)}
                />
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default History;
